package whileloop;

public class whilelooptest {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int y=1;
		int z=10;
		
		do {
			
			System.out.println(y);
			
			y++;
			
			
		} while (y<=z);
		
		
		int x = 1;
		int yy1 = 10;
		int zz1 = 15;
		int a=1;
			
		while (x<=yy1) {
		System.out.println(x);
		x++;
	
		}
		
	System.out.println("i am from 2nd line");
	
	
		while (a<=zz1) {
			System.out.println(a);
			a++;
		
		}	
		
	}

		
		
	}

